mapper.insert:
insert into mr_room (id, building_no, floor_no, 
  room_no, room_size, pic, 
  create_time, update_time, deleted
  )
values (#{id,jdbcType=BIGINT}, #{buildingNo,jdbcType=VARCHAR}, #{floorNo,jdbcType=VARCHAR}, 
  #{roomNo,jdbcType=VARCHAR}, #{roomSize,jdbcType=INTEGER}, #{pic,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{deleted,jdbcType=TINYINT}
  )

mapper.select:
select

from mr_room
where building_no = #{buildingNo,jdbcType=VARCHAR} and floor_no = #{floorNo,jdbcType=VARCHAR}
